import { createReducer, on } from '@ngrx/store';
import { createAccountActionSuccess, getDetailsAccountActionSuccess } from '../actions/account.actions';
import { initialAccountState } from '../state/app.state';

const _accountReducer = createReducer(initialAccountState, on(createAccountActionSuccess, (state, action) => {
  return Object.assign(Object.assign({}, state), {
    account: action.account
  });
}), on(getDetailsAccountActionSuccess, (state, action) => {
  return Object.assign(Object.assign({}, state), {
    account: action.account
  });
}));

export function accountReducer(state, action) {
  return _accountReducer(state, action);
