import { createSelector } from '@ngrx/store';

const selectUser = state => {
  return state.userState;
};

export const selectUserResponse = createSelector(selectUser, state => state.userAuth);
export const selectUserResponseMessage = createSelector(selectUser, state => state.token);
export const selectUserAuth = createSelector(selectUser, state => state.user);
export const selectUserLoading = createSelector(selectUser, state => state.loading);
export const selectSingleUser = createSelector(selectUser, state => state.singleUser);
