import { createReducer, on } from '@ngrx/store';
import { AddOrderActionSuccess } from 'store/actions/order.action';
import { initialOrderState } from 'store/state/app.state';

const _orderReducer = createReducer(initialOrderState, on(AddOrderActionSuccess, (state, action) => {
  return Object.assign(Object.assign({}, state), {
    orders: [action.item, action.user_id]
  });
}));

export function orderReducer(state, action) {
  return _orderReducer(state, action);
}
