import { AddCartItemActionSuccess, GetCartItemActionSuccess } from '../actions/cart.action';
import { initialCartState } from '../state/app.state';
import { createReducer, on } from '@ngrx/store';

const _cartReducer = createReducer(initialCartState, on(AddCartItemActionSuccess, (state, action) => {
  return Object.assign(Object.assign({}, state), {
    cart: action.products
  });
}), on(GetCartItemActionSuccess, (state, action) => {
  return Object.assign(Object.assign({}, state), {
    cart: action.item
  });
}));

export function cartReducer(state, action) {
  return _cartReducer(state, action);
}
