import { createSelector } from '@ngrx/store';

const selectCart = state => {
  return state.cartState;
};

export const selectCartList = createSelector(selectCart, state => {
  return state.cartWithProducts;
});
export const selectCartTotal = createSelector(selectCart, state => {
  return state.total;
});
export const selectSubTotalsCart = createSelector(selectCart, state => {
  return state.subTotals;
});
