import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { User } from 'src/app/model/user';
import { ToastrService } from 'ngx-toastr';
import {select, Store} from '@ngrx/store';
import { UserLoginAction, UserSignUpAction} from 'store/actions/user.action';
import { IAppState } from 'store/state/app.state';
import { selectUserResponse } from 'store/selectors/user.selector';

@Component({
  selector: 'app-sign-in',
  templateUrl: './sign-in.component.html',
  styleUrls: ['./sign-in.component.css']
})
export class SignInComponent implements OnInit {

  user?: User;

  formGroup: FormGroup;

  formUserSignIn: FormGroup;

  token:any;

  data:any;

  user$: any;

  constructor(private toastr: ToastrService,  private fb: FormBuilder, private store: Store<IAppState>) {  
    this.formGroup = this.fb.group({
      name: "",
      email: "",
      password: ""
    }) 

    this.formUserSignIn = this.fb.group({
      name: "",
      password: ""
    })    
  }

  ngOnInit(): void {
    this.user$ = this.store.select<User[]>(selectUserResponse);
  }

  //signUp(): void {
  //  let formUser: User =  this.formGroup?.value;
  //  console.log(formUser);
  //  this.user_service.signUp(formUser).subscribe(res =>{
  //   console.log(res);
  //  });
 // }
