 //   let formSignIn: User =  this.formGroupSignIn?.value;
  //  this.user_service.signIn(formSignIn).subscribe(res =>{
    //  this.data = res;
     // if(this.data.status === 1){
    //    this.toastr.success(JSON.stringify(this.data.message),JSON.stringify(this.data.code),{
    //      timeOut: 2000,
    //      progressBar: true,

   //     });
     //   this.token = this.data.data.token;
    //    localStorage.setItem('token', JSON.stringify(this.token));
       // this.router.navigate(['/']);
     // } else{
    //    this.toastr.error(JSON.stringify(this.data.message),JSON.stringify(this.data.code),{
     //     timeOut: 2000,
     //     progressBar: true,
    //    })
    //  }
   // });
  //}

  signUp(){
    let formUser: User =  {
      'name' : this.formGroup.value.name,
      'email' : this.formGroup.value.email,
      'password' : this.formGroup.value.password,
     }
        this.store.dispatch(UserSignUpAction({user : formUser}));
  }


  signIn(){
    let form: User = this.formUserSignIn.value;   
      this.store.dispatch(UserLoginAction({user : form}));
  }
}
