import { createSelector } from '@ngrx/store';

const selectAccount = state => {
  return state.accountState;
};

export const selectAccountList = createSelector(selectAccount, state => {
  return state.account;
});
export const selectAccountLoading = createSelector(selectAccount, state => {
  return state.loading;
});
export const selectSingleAccount = createSelector(selectAccount, state => {
  return state.singleAccount;
});
