import { GetProductsAction, GetSingleProductAction, ProductsTypeActionSuccess } from '../actions/products.action';
import { initialProductsState } from '../state/app.state';
import { createReducer, on } from '@ngrx/store';

const _productsReducer = createReducer(initialProductsState, on(ProductsTypeActionSuccess, (state, action) => {
  return Object.assign(Object.assign({}, state), {
    products: action.products
  });
}), on(GetProductsAction, (state, action) => {
  return Object.assign(Object.assign({}, state), {
    products: action.products
  });
}), on(GetSingleProductAction, (state, action) => {
  return Object.assign(Object.assign({}, state), {
    products: action.products
  });
}));

export function productsReducer(state, action) {
  return _productsReducer(state, action);
}
