import { createSelector } from '@ngrx/store';
import { selectRouteParams } from './router.selector';

const selectProducts = state => {
  return state.productsState;
};

export const selectProductList = createSelector(selectProducts, state => {
  return state.products;
});
export const selectProductById = createSelector(selectProductList, selectRouteParams, (products, {
  id
}) => {
  return products[id];
});
